b=[94.8194;0;0;0;0;21];r=[1.5;1.42;1.32;1.23;1.15;1.07];c=[20;17;23;24;25;21];a=[0;0;0;0;0;0];z=[0;0;0;0;0;0];jahre=[1996;1997;1998;1999;2000;2001];%b=c;a(1)=b(1)-c(1)for i=2:6  a(i)=b(i)-c(i)+a(i-1)*1.069;  z(i)=a(i-1)*0.069;endforplot(jahre,a,'r')hold onplot(jahre,b,'g')plot(jahre,c,'b')plot(jahre,z,'k')legend('Konto','Anleihen','Ausgaben','Zinsen')xlabel('Jahre')ylabel('10E6 Euro')summeRueckZahl=sum(b.*r);title(summeRueckZahl)anualRate=[nthroot(1.50,5); nthroot(1.42,4);nthroot(1.32,3);nthroot(1.23,2);nthroot(1.15,1);0.07];